import logging
import re
import traceback

from sys import platform
from typing import Dict, List

import GPUtil
import PySimpleGUI as Sg

from lang import (GuiField, get_available_languages_name,
                  get_current_language_name, get_text, set_current_language)
from ytdlp_handler import *


def _get_encoders_list() -> dict:
    encoders_dict = dict()
    encoders_dict.update({"x264": list(), "x265": list(), "ProRes": ["prores"], "AV1": ["libaom-av1"]})
    tmp_list_x264 = encoders_dict["x264"]
    tmp_list_x265 = encoders_dict["x265"]
    gpus = GPUtil.getGPUs()
    for gpu in gpus:
        gpu_name = gpu.name
        if re.match("NVIDIA", gpu_name):
            tmp_list_x264.append("h264_nvenc")
            tmp_list_x265.append("hevc_nvenv")
        if re.match("AMD", gpu_name):
            tmp_list_x264.append("h264_amf")
            tmp_list_x265.append("hevc_amf")
        if re.match("Intel", gpu_name):
            tmp_list_x264.append("h264_qsv")
            tmp_list_x265.append("hevc_qsv")
    tmp_list_x264.append("libx264")
    tmp_list_x265.append("libx265")
    encoders_dict["x264"] = tmp_list_x264
    encoders_dict["x265"] = tmp_list_x265
    return encoders_dict


gpus_possible_encoders = _get_encoders_list()
icon_base64 = b''


def _video_dl() -> None:
    download_path = _get_download_path()
    Sg.theme('DarkGrey13')
    layout = [
        [Sg.Combo(get_available_languages_name(), default_value=get_current_language_name(), enable_events=True,
                  readonly=True, key="Lang")],
        [Sg.Text(get_text(GuiField.link), key="TextLink")],
        [Sg.Input(key="url")],
        [Sg.Text(get_text(GuiField.destination), key="TextDestination")],
        [Sg.Input(download_path, key="path"),
         Sg.FolderBrowse(button_text="...")],
        [Sg.Checkbox(get_text(GuiField.start), default=False, checkbox_color="black", enable_events=True, key="Start"),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="sH", enable_events=True,
                  default_text="00"),
         Sg.Text(":", size=(1, 1), pad=(0, 0)),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="sM", enable_events=True,
                  default_text="00"),
         Sg.Text(":", size=(1, 1), pad=(0, 0)),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="sS", enable_events=True,
                  default_text="00")],
        [Sg.Checkbox(get_text(GuiField.end), default=False, checkbox_color="black", enable_events=True, key="End"),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="eH", enable_events=True,
                  default_text="99"),
         Sg.Text(":", size=(1, 1), pad=(0, 0)),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="eM", enable_events=True,
                  default_text="59"),
         Sg.Text(":", size=(1, 1), pad=(0, 0)),
         Sg.Input(size=(4, 1), disabled=True, disabled_readonly_background_color="gray", key="eS", enable_events=True,
                  default_text="59")],
        [Sg.Text(get_text(GuiField.quality), key="TextQuality")],
        [Sg.Combo(['4320p', '2160p', '1440p', '1080p', '720p', '480p'],
                  default_value='1080p', readonly=True, key="MaxHeight")],
        [Sg.Text(get_text(GuiField.framerate), key="TextFramerate")],
        [Sg.Combo(['60', '30'], default_value='60', readonly=True, key="MaxFPS")],
        [Sg.Text(get_text(GuiField.vcodec), key="TextVCodec")],
        [Sg.Combo(['x264', 'x265', 'ProRes', 'AV1'], default_value='x264', readonly=True, key="TargetCodec")],
        [Sg.Checkbox(get_text(GuiField.audio_only), default=False, checkbox_color="black",
                     enable_events=True, key="AudioOnly")],
        [Sg.Text(get_text(GuiField.cookies), key="TextCookies")],
        [Sg.Combo(['None', 'Brave', 'Chrome', 'Chromium', 'Edge', 'Firefox', 'Opera', 'Safari', 'Vivaldi'],
                  default_value='None', readonly=True, key="Browser")],
        [Sg.Button(get_text(GuiField.dl_button), enable_events=True, key="dl"),
         Sg.Text(key="error", text_color="red", visible=False)]
    ]
    window = Sg.Window('Video-dl', layout=layout, icon=icon_base64)

    while True:
        event, values = window.Read()
        if event in (Sg.WIN_CLOSED, 'Exit'):
            break
        window["error"].update(visible=False)
        _fill_timecode(values, window)
        if event == "Start" or event == "End":
            _trim_checkbox(values, window, event)
        elif event == "AudioOnly":
            _audio_only_checkbox(values, window)
        elif event == "Lang":
            _change_language(values, window)
        elif event == "dl":
            if values["Start"] and values["End"] and _check_timecode(values, window):
                window["error"].update(get_text(GuiField.incorrect_timestamp), visible=True, text_color="yellow")
            elif values["path"] == '':
                window["error"].update(get_text(GuiField.missing_output), visible=True)
            else:
                window["error"].update(visible=False)
                # noinspection PyBroadException
                try:
                    video_dl(values)
                except ValueError:
                    logging.error(traceback.format_exc())
                    window["error"].update(get_text(GuiField.dl_cancel), visible=True, text_color="yellow")
                except FileExistsError:
                    window["error"].update(get_text(GuiField.dl_finish), visible=True, text_color="green")
                except yt_dlp.utils.DownloadError as e:
                    logging.error(traceback.format_exc())
                    window["error"].update(get_text(GuiField.dl_unsupported_url) + str(e), visible=True, text_color="red")
                except Exception as e:
                    logging.error(traceback.format_exc())
                    window["error"].update(f"{get_text(GuiField.dl_error)}\n{str(e)}\n{traceback.format_exc()}", visible=True, text_color="red")
                else:
                    window["error"].update(get_text(GuiField.dl_finish), visible=True, text_color="green")


def _audio_only_checkbox(values: Dict, window: Sg.Window) -> None:
    audio_checkbox = values["AudioOnly"]
    window["MaxHeight"].update(disabled=audio_checkbox)
    window["MaxFPS"].update(disabled=audio_checkbox)
    window["TargetCodec"].update(disabled=audio_checkbox)


def _fill_timecode(values: Dict, window: Sg.Window) -> None:
    values2check = ["sH", "sM", "sS", "eH", "eM", "eS"]
    for value in values2check:
        for char in values[value]:
            if char not in '0123456789':
                window[value].update(values[value].replace(char, ""))
                values[value] = values[value].replace(char, "")
        if len(values[value]) > 2:
            window[value].update(values[value][1:])
            values[value] = values[value][1:]
        elif len(values[value]) == 1:
            window[value].update("0" + values[value][0])
            values[value] = "0" + values[value][0]
        elif len(values[value]) == 0:
            window[value].update("00")
            values[value] = "00"
        if value != 'sH' and value != 'eH' and len(values[value]) > 0 and values[value] != ' ' and int(values[value]) > 59:
            window[value].update('0' + values[value][-1])
            values[value] = '0' + values[value][-1]


def _check_timecode(values: Dict, window: Sg.Window) -> bool:
    sH, sM, sS = int(values["sH"]), int(values["sM"]), int(values["sS"])
    eH, eM, eS = int(values["eH"]), int(values["eM"]), int(values["eS"])
    return sH > eH or (sH == eH and sM > eM) or (sH == eH and sM == eM and sS > eS)


def _trim_checkbox(values: Dict, window: Sg.Window, index: str) -> None:
    disabled = not values[index]
    if index == "Start":
        window["sH"].update(disabled=disabled, value="00")
        window["sM"].update(disabled=disabled, value="00")
        window["sS"].update(disabled=disabled, value="00")
        return
    elif index == "End":
        window["eH"].update(disabled=disabled, value="99")
        window["eM"].update(disabled=disabled, value="59")
        window["eS"].update(disabled=disabled, value="59")
        return


def _get_download_path() -> str:
    if platform == "win32":
        import winreg
        sub_key = r'SOFTWARE\Microsoft\Windows\CurrentVersion\Explorer\Shell Folders'
        downloads_guid = '{374DE290-123F-4565-9164-39C4925E467B}'
        with winreg.OpenKey(winreg.HKEY_CURRENT_USER, sub_key) as key:
            location = winreg.QueryValueEx(key, downloads_guid)[0]
        return location
    else:
        return ''


def _change_language(values: Dict, window: Sg.Window) -> None:
    """
    Updates current language and update the window's text fields.
    """
    set_current_language(values["Lang"])
    _update_text_lang(window)


def _update_text_lang(window: Sg.Window) -> None:
    """
    Update the text of each element on the layout.

    Note: Checkboxes elements need "text=" to be specified.
    """
    window["TextLink"].update(get_text(GuiField.link))
    window["TextDestination"].update(get_text(GuiField.destination))
    window["Start"].update(text=get_text(GuiField.start))
    window["End"].update(text=get_text(GuiField.end))
    window["TextQuality"].update(get_text(GuiField.quality))
    window["TextFramerate"].update(get_text(GuiField.framerate))
    window["AudioOnly"].update(text=get_text(GuiField.audio_only))
    window["TextCookies"].update(get_text(GuiField.cookies))
    window["dl"].update(get_text(GuiField.dl_button))
    return


if __name__ == '__main__':
    _video_dl()

